import { Row, Col, Empty } from "antd";

import Card from "../common/Card";
import UserCard from "../common/UserCard";
import CharacterCard from "../common/CharacterCard";
import LocationCard from "../common/LocationCard";
import WithLink from "../hoc/WithLink";

const StoriesGrid = ({ stories, type, gutter, lg, md, sm, xs }) => {
  return (
    <Row gutter={gutter}>
      {stories.map((story) => (
        <Col key={story.id} md={md} xs={xs} sm={sm} lg={lg} xl={xl}>
          <Card story={story} type={type} />
        </Col>
      ))}
    </Row>
  );
};

const UserGrid = () => {
  return (
    <Row gutter={[20, 30]} align="center">
      <Col lg={4} md={6} sm={8} xs={12}>
        <UserCard />
      </Col>
      <Col lg={4} md={6} sm={8} xs={12}>
        <UserCard />
      </Col>
      <Col lg={4} md={6} sm={8} xs={12}>
        <UserCard />
      </Col>
      <Col lg={4} md={6} sm={8} xs={12}>
        <UserCard />
      </Col>
      <Col lg={4} md={6} sm={8} xs={12}>
        <UserCard />
      </Col>
      <Col lg={4} md={6} sm={8} xs={12}>
        <UserCard />
      </Col>
      <Col lg={4} md={6} sm={8} xs={12}>
        <UserCard />
      </Col>
      <Col lg={4} md={6} sm={8} xs={12}>
        <UserCard />
      </Col>
    </Row>
  );
};

const LocationGrid = ({ locations, gutter, xl, xxl, lg, md, sm, xs, type }) => {
  return locations.length > 0 ? (
    <Row gutter={gutter}>
      {locations.map((loc) => (
        <Col xl={xl} xxl={xxl} xs={xs} sm={sm} md={md} lg={lg} key={loc.id}>
          <LocationCard type={type} location={loc} />
        </Col>
      ))}
    </Row>
  ) : (
    <Empty
      image={Empty.PRESENTED_IMAGE_SIMPLE}
      description="No locations yet"
    />
  );
};

const CharacterGrid = ({
  gutter,
  xxl,
  xl,
  lg,
  md,
  sm,
  xs,
  characters,
  type,
}) => {
  return characters.length > 0 ? (
    <Row gutter={gutter}>
      {characters?.map((c) => (
        <Col key={c.id} xxl={xxl} xl={xl} lg={lg} md={md} sm={sm} xs={xs}>
          {type === "show" ? (
            <WithLink link={`/character/${c.id}`}>
              <CharacterCard character={c} type={type} />
            </WithLink>
          ) : (
            <CharacterCard character={c} type={type} />
          )}
        </Col>
      ))}
    </Row>
  ) : (
    <Empty
      image={Empty.PRESENTED_IMAGE_SIMPLE}
      description="No characters yet"
    />
  );
};

export { StoriesGrid, CharacterGrid, UserGrid, LocationGrid };
