import React from "react";
import { Form } from "antd";
import { connect } from "react-redux";

import { useAuth } from "../../hooks/userHooks";
import { getUserCharacters } from "../../redux/actions/charactersActions";

import LoadingScreen from "../../components/hoc/LoadingScreen";
import RedirectComp from "../../components/hoc/RedirectComp";
import CharacterForm from "../../components/forms/CharacterForm";

const newCharacter = (props) => {
  const { characters } = props;
  const auth = useAuth();
  const [form] = Form.useForm();

  const onFailed = () => {
    window.scrollTo(0, 0);
    message.error("There are some errors");
  };

  const submit = (values) => {
    console.log(values);
  };

  return (
    <LoadingScreen loading={auth.isLoading}>
      <RedirectComp condition={auth.user} type="redirect">
        <div className="new-character custom-form">
          <div className="inner">
            <h2 className="side-heading">Add a new character</h2>
            <CharacterForm
              submit={submit}
              onFailed={onFailed}
              form={form}
              characters={characters}
            />
          </div>
        </div>
      </RedirectComp>
    </LoadingScreen>
  );
};

const mapStateToProps = (state) => ({
  characters: state.characters.userCharacters,
});

export default connect(mapStateToProps, { getUserCharacters })(newCharacter);
