import React from "react";
import Link from "next/link";
import { useRouter } from "next/router";
import { Row, Col, Image, Tag, Skeleton, Space, message } from "antd";
import { connect } from "react-redux";
import moment from "moment";

import { LANGUAGES } from "../../../utils/constants";
import {
  getStory,
  getChapters,
  getStoryCharacters,
  deleteChapter,
  isStoryFavorite,
  addStoryToFavorite,
} from "../../../redux/actions/storiesActions";

import CharacterCard from "../../../components/common/CharacterCard";
import LocationCard from "../../../components/common/LocationCard";
import WithLink from "../../../components/hoc/WithLink";
import LoadingScreen from "../../../components/hoc/LoadingScreen";
import Chapters from "../../../components/story/Chapters";
import Like from "../../../components/common/Like";
import { useAuth } from "../../../hooks/userHooks";

const Story = ({
  getStory,
  story,
  loading,
  getChapters,
  chapters,
  getStoryCharacters,
  deleteChapter,
  actionMessage,
  isStoryFavorite,
  isFavorite,
  addStoryToFavorite,
}) => {
  const router = useRouter();
  const auth = useAuth();

  React.useEffect(() => {
    getStory(router.query.id);
    getChapters(router.query.id);
    getStoryCharacters(router.query.id);
    isStoryFavorite(router.query.id);
  }, [router.query.id]);

  React.useEffect(() => {
    if (actionMessage) {
      message.success(actionMessage);
      getChapters(router.query.id);
    }
  }, [actionMessage]);

  const deleteChapterFunc = (id) => {
    deleteChapter(id, router.query.id);
  };

  console.log(isFavorite);

  return (
    <LoadingScreen loading={loading || auth.isLoading}>
      <div className="story">
        <Row gutter={24}>
          <Col sm={24} md={8} lg={6}>
            <div className="story-details">
              {!loading && (
                <>
                  <div className="poster" data-aos="zoom-in">
                    <Image
                      src={story.banner}
                      alt={`poster ${story.title}`}
                      width={"100%"}
                      height={"100%"}
                    />
                  </div>
                  <h2>{story.title}</h2>
                  <p className="author">
                    by{" "}
                    <Link href="/profile/1">
                      <a>{story.authorName}</a>
                    </Link>
                  </p>
                  <Like
                    id={router.query.id}
                    title={story.title}
                    type="story"
                    isFavorite={isFavorite}
                    likeFunc={addStoryToFavorite}
                    username={auth.user.username}
                  />
                  <h3>Summary</h3>
                  <p className="summary">
                    {story.summary ? story.summary : "No summary yet"}
                  </p>
                  <h3>Details</h3>
                  <div className="details">
                    <span>
                      Posted on:{" "}
                      {typeof story.createdAt === "object"
                        ? moment
                            .unix(story.createdAt.seconds)
                            .format("YYYY-DD-MM")
                        : moment(story.createdAt).format("YYYY-DD-MM")}
                    </span>
                    <br />
                    <span>
                      Category:{" "}
                      <Link href="/category/1">
                        <a>Drama</a>
                      </Link>
                    </span>
                    <br />
                    <span>
                      Language:{" "}
                      {LANGUAGES.find((l) => l.value === story.language).name}
                    </span>
                    <br />
                    <span>Status: {story.status}</span>
                    <br />
                    <span>Copyright: {story.copyright}</span>
                    <br />
                    <span>Cover Copyright: {story.imageCopyright}</span>
                    <br />
                    <span>Mature content: {story.mature ? "YES" : "NO"}</span>
                    <br />
                  </div>
                  <h3>Tags</h3>
                  <div className="tags">
                    {story.tags.map((tag) => (
                      <Tag key={tag} color="#6d5dfc">{`#${tag}`}</Tag>
                    ))}
                  </div>
                </>
              )}
              <>
                {loading && (
                  <Skeleton.Avatar
                    loading={loading}
                    shape="square"
                    size={265}
                    active
                  />
                )}
                <Skeleton loading={loading} paragraph active />
                <Skeleton loading={loading} paragraph active />
              </>
            </div>
          </Col>
          <Col sm={24} md={16} lg={18}>
            <div className="story-chapters">
              <Chapters
                chapters={chapters}
                id={router.query.id}
                deleteChapter={deleteChapterFunc}
              />
              <h3 className="heading">Main Characters</h3>
              <Row gutter={[24, 16]}>
                {story.mainCharacters?.map((c) => (
                  <Col key={c.id} xs={12} sm={8} md={6} lg={4}>
                    <WithLink link={`/character/${c.id}`}>
                      <CharacterCard character={c} type="story" />
                    </WithLink>
                  </Col>
                ))}
              </Row>
              <h3 className="heading">Secondary Characters</h3>
              <Row gutter={[24, 16]}>
                {story.secondaryCharacters?.map((c) => (
                  <Col key={c.id} xs={12} sm={8} md={6} lg={4}>
                    <WithLink link={`/character/${c.id}`}>
                      <CharacterCard character={c} type="story" />
                    </WithLink>
                  </Col>
                ))}
              </Row>
              <h3 className="heading">Locations</h3>
              <Row gutter={[24, 16]}>
                <Col xs={12} sm={8} md={6} lg={4}>
                  <LocationCard />
                </Col>
                <Col xs={12} sm={8} md={6} lg={4}>
                  <LocationCard />
                </Col>
                <Col xs={12} sm={8} md={6} lg={4}>
                  <LocationCard />
                </Col>
                <Col xs={12} sm={8} md={6} lg={4}>
                  <LocationCard />
                </Col>
                <Col xs={12} sm={8} md={6} lg={4}>
                  <LocationCard />
                </Col>
                <Col xs={12} sm={8} md={6} lg={4}>
                  <LocationCard />
                </Col>
              </Row>
            </div>
          </Col>
        </Row>
      </div>
    </LoadingScreen>
  );
};

const mapStateToProps = (state) => ({
  story: state.stories.story,
  loading: state.stories.loading,
  chapters: state.stories.chapters,
  actionMessage: state.stories.message,
  loadingChapter: state.stories.loadingChapter,
  isFavorite: state.stories.isFavorite,
});

export default connect(mapStateToProps, {
  getStory,
  getChapters,
  getStoryCharacters,
  deleteChapter,
  isStoryFavorite,
  addStoryToFavorite,
})(Story);
