import React from "react";
import Router from "next/router";

const Slide = (props) => {
  const slide = React.useRef();
  const { banner, title, index, id } = props.slide;
  const current = props.current;
  let classNames = "slide";

  React.useEffect(() => {
    console.log(current);
    if (current === index) classNames += " slide--current";
    else if (current - 1 === index) classNames += " slide--previous";
    else if (current + 1 === index) classNames += " slide--next";
  }, [current]);

  const handleMouseMove = (event) => {
    const el = slide.current;
    const r = el.getBoundingClientRect();

    el.style.setProperty(
      "--x",
      event.clientX - (r.left + Math.floor(r.width / 2))
    );
    el.style.setProperty(
      "--y",
      event.clientY - (r.top + Math.floor(r.height / 2))
    );
  };

  const handleMouseLeave = (event) => {
    slide.current.style.setProperty("--x", 0);
    slide.current.style.setProperty("--y", 0);
  };

  const handleSlideClick = (event) => {
    props.handleSlideClick(props.slide.id);
  };

  const imageLoaded = (event) => {
    event.target.style.opacity = 1;
  };

  return (
    <li
      ref={slide}
      className={classNames}
      onClick={handleSlideClick}
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
    >
      <div className="slide__image-wrapper">
        <img
          className="slide__image"
          alt={title}
          src={banner}
          onLoad={imageLoaded}
        />
      </div>

      <article className="slide__content">
        <h2 className="slide__headline">{title}</h2>
        <button
          onClick={() => Router.push(`/story/${id}`)}
          className="slide__action btn"
        >
          Read
        </button>
      </article>
    </li>
  );
};

export default Slide;
