import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { Row, Modal, Button } from "antd";

import Banner from "../../components/profile/Banner";
import Tabs from "../../components/profile/Tabs";
import Stories from "../../components/profile/Stories";
import Characters from "../../components/profile/Characters";
import Followers from "../../components/profile/Followers";
import Locations from "../../components/profile/Locations";
import Settings from "../../components/profile/Settings";

import { getProfile } from "../../redux/actions/userActions";
import { getUserStories } from "../../redux/actions/storiesActions";
import { useAuth } from "../../hooks/userHooks";

const Profile = ({ getProfile, profile, stories }) => {
  const [tabs] = useState([
    "stories",
    "characters",
    "locations",
    "favorites stories",
    "favorites characters",
  ]);
  const [modalVisible, setModalVisible] = useState(false);
  const [settings, openSettings] = useState(false);
  const [currentTab, setCurrentTab] = useState("stories");
  const changeTab = (tab) => setCurrentTab(tab);

  const auth = useAuth();

  useEffect(() => {
    if (auth.user) {
      getProfile();
      getUserStories();
    }
  }, [auth.user]);

  console.log(stories);

  return (
    <div className="profile">
      <Banner
        profile={profile}
        openSettings={openSettings}
        setModalVisible={setModalVisible}
      />
      <section className="profile-content">
        <Tabs currentTab={currentTab} tabs={tabs} changeTab={changeTab} />
        {currentTab === "stories" && <Stories />}
        {currentTab === "characters" && <Characters />}
        {currentTab === "locations" && <Locations />}
        {currentTab === "favorites stories" && <Stories />}
        {currentTab === "favorites characters" && <Characters />}
      </section>
      <Modal
        title="128 Followers"
        width={"50%"}
        visible={modalVisible}
        onOk={() => setModalVisible(false)}
        onCancel={() => setModalVisible(false)}
        footer={[
          <Button key="back" onClick={() => setModalVisible(false)}>
            OK
          </Button>,
        ]}
      >
        <Row>
          <Followers />
        </Row>
      </Modal>
      <Modal
        title="Settings"
        visible={settings}
        onOk={() => openSettings(false)}
        onCancel={() => openSettings(false)}
        footer={[
          <Button
            key="submit"
            type="primary"
            onClick={() => openSettings(false)}
          >
            Submit
          </Button>,
        ]}
      >
        <Settings />
      </Modal>
    </div>
  );
};

const mapStateToProps = (state, ownProps) => {
  return { profile: state.auth.profile, stories: state.stories.stories };
};

export default connect(mapStateToProps, { getProfile, getUserStories })(
  Profile
);
