import React from "react";
import Link from "next/link";
import { Row, Col, Space, Empty } from "antd";

const Notifications = ({ notif }) => {
  return (
    <div className="notifications">
      {notif.items.length > 0 ? (
        notif.items.map((n) => (
          <div className="notification" data-aos="flip-left" key={n.id}>
            <Row align="middle" gutter={60} justify="space-between">
              <Col>
                <Link href="/story/1">
                  <a className="notification-desc">
                    <Space size={16}>
                      <div className="notification-avatar">
                        <img src={n.user.image} alt={n.user.username} />
                      </div>
                      <p>{n.message}</p>
                    </Space>
                  </a>
                </Link>
              </Col>

              <Col>
                <div className="notifications-buttons">
                  <Space size={20}>
                    <span className="btn mark-read-btn">Mark as Read</span>
                    <span className="btn delete-btn">Delete</span>
                  </Space>
                </div>
              </Col>
            </Row>
          </div>
        ))
      ) : (
        <Empty
          image={Empty.PRESENTED_IMAGE_SIMPLE}
          description="No notifications yet"
        />
      )}
    </div>
  );
};

export default Notifications;
