import { GET_USER_STORIES } from "../../utils/constants";
import { db, auth } from "../fbConfig";

export const getUserStories = () => (dispatch) => {
  db.collection("stories")
    .where("authorId", "==", auth.currentUser.uid)
    .get()
    .then((docs) => {
      let items = [];

      docs.forEach((doc) => {
        items = [...items, { id: doc.id, ...doc.data() }];
      });

      return items;
    })
    .then((items) => {
      const newItems = items.map((item) => {
        let newItem = { ...item, categories: [] };
        const categories = item.category.map((cat) => {
          return db
            .collection("categories")
            .doc(cat)
            .get()
            .then((doc) => {
              return doc.data().name;
            });
        });
        Promise.all(categories).then((res) => ({
          ...newItem,
          categories: res,
        }));
      });

      // items.foreach((item) => {
      //   item.category.forEach((cat) =>
      //     db
      //       .collection("categories")
      //       .doc(cat)
      //       .get((doc) => (categories = [...categories, doc.data().name]))
      //   );
      //   newItems = [...newItems, { ...item, categories }];
      // });

      console.log(newItems);
      return items;
    })
    .then((items) => {
      dispatch({ type: GET_USER_STORIES, payload: items });
    });
};
