import React from "react";

const Comments = ({ comments }) => {
  const [visible, setVisible] = React.useState(false);

  return (
    <>
      <section className="character-feedback">
        <header className="feedback-header">
          <h3 className="side-heading">Feedback</h3>
          <div className="add-comment">
            <ion-icon name="add-circle"></ion-icon>
            <span>Leave a comment</span>
          </div>
        </header>
      </section>
      <Modal
        title={"Leave a new comment"}
        visible={visible}
        onCancel={() => setVisible(false)}
        footer={[
          <Button key="submit" type="primary" onClick={() => setVisible(false)}>
            Submit
          </Button>,
        ]}
      >
        <img
          className="modal-img"
          src={location.image ? location.image : dummy.noImage}
          alt={location.name}
        />
        <p>{location.description}</p>
        {type !== "story" && (
          <p className="modal-story-link">
            Story:{" "}
            <Link href={`/story/${location.storyId}`}>
              <a>{location.storyTitle}</a>
            </Link>
          </p>
        )}
      </Modal>
    </>
  );
};

export default Comments;
