import React from "react";
import { Form, Col, Row, Select, Divider } from "antd";

const { Option } = Select;

const CharacterSelector = ({
  item,
  characters,
  form,
  arrInForm,
  label,
  type,
}) => {
  const [currentChar, setCurrentChar] = React.useState("");
  const [selectedCharacters, setSelectedCharacters] = React.useState(
    item ? characters.filter((c) => item.includes(c.id)) : []
  );

  const addCharacter = () => {
    if (
      currentChar &&
      form.getFieldValue(arrInForm)?.indexOf(currentChar) === -1 &&
      form.getFieldValue(arrInForm)?.length < 5
    ) {
      setSelectedCharacters([
        ...selectedCharacters,
        characters.find((c) => c.id === currentChar),
      ]);
      form.setFieldsValue({
        [arrInForm]: [...form.getFieldValue(arrInForm), currentChar],
      });
    }
    setCurrentChar("");
    form.setFieldsValue({ currentChar: "" });
  };

  const removeCharacter = (id) => {
    form.setFieldsValue({
      [arrInForm]: form.getFieldValue(arrInForm).filter((c) => c !== id),
    });
    setSelectedCharacters(selectedCharacters.filter((c) => c.id !== id));
  };

  return (
    <Form.Item name={arrInForm} label={label}>
      <Row gutter={[24, 24]}>
        <Col lg={12} md={24} sm={24} xs={24}>
          <Row gutter={16}>
            <Col span={22}>
              <Form.Item name="currentChar">
                <Select
                  disabled={
                    form.getFieldValue(arrInForm)?.length >= 5 ? true : false
                  }
                  onChange={(val) => setCurrentChar(val)}
                >
                  <Option value="none" disabled>
                    Choose
                  </Option>
                  {characters
                    .filter(
                      (c) => form.getFieldValue(arrInForm)?.indexOf(c.id) === -1
                    )
                    ?.sort((a, b) => {
                      const keyA = a.firstname,
                        keyB = b.firstname;
                      // Compare the 2 dates
                      if (keyA < keyB) return -1;
                      if (keyA > keyB) return 1;
                      return 0;
                    })
                    .map((char) => (
                      <Option
                        key={char.id}
                        value={char.id}
                      >{`${char.firstname} ${char.lastname}`}</Option>
                    ))}
                </Select>
              </Form.Item>
            </Col>
            <Col span={2}>
              <div className="select-add-input-iconbox" onClick={addCharacter}>
                <span>+</span>
              </div>
            </Col>
            {type === "story" && (
              <small>You can add up to 5 main characters</small>
            )}
          </Row>
        </Col>
        <Col lg={12} md={24} sm={24} xs={24}>
          <Row gutter={16}>
            {characters
              .filter((c) => form.getFieldValue(arrInForm).includes(c.id))
              .map((char) => (
                <Col md={12} sm={24} key={char.id}>
                  <div key={char.id} className="main-selected">
                    <div className="main-avatar">
                      <img src={char.image} alt={char.firstname} />
                    </div>
                    <p>{`${char.firstname} ${char.lastname}`} </p>
                    <ion-icon
                      onClick={() => removeCharacter(char.id)}
                      name="close-circle"
                    ></ion-icon>
                  </div>
                  <Divider />
                </Col>
              ))}
          </Row>
        </Col>
      </Row>
    </Form.Item>
  );
};

export default CharacterSelector;
