import React from "react";
import { Row, Col, Divider, Form, Spin, message, Input, Select } from "antd";
import { connect } from "react-redux";
import { useRouter } from "next/router";
import isImageUrl from "is-image-url";

import {
  CATEGORIES,
  COPYRIGHTS,
  LANGUAGES,
  STATUS,
} from "../../../utils/constants";
import { useAuth } from "../../../hooks/userHooks";
import { getUserCharacters } from "../../../redux/actions/charactersActions";
import { getStory, editStory } from "../../../redux/actions/storiesActions";
import Loader from "../../../components/common/Loader";

const EditStory = (props) => {
  const auth = useAuth();
  const router = useRouter();
  const { story, loading } = props;
  const [form] = Form.useForm();

  // React.useEffect(() => {
  //   if (auth.user) {
  //     // props.getUserCharacters();
  //   }
  // }, [auth]);

  React.useEffect(() => {
    if (router.query.id) {
      props.getStory(router.query.id);
    }
  }, [router.query.id]);

  const submit = (values) => {
    console.log(values);
  };
  const onFailed = () => {};

  console.log(story.title);

  return !loading && story.title ? (
    <div className="new-story custom-form">
      <div className="inner">
        <h2 className="side-heading">Edit story: {story.title}</h2>
        <Form
          layout="vertical"
          scrollToFirstError
          onFinishFailed={onFailed}
          onFinish={submit}
          form={form}
          initialValues={{ ...story }}
        >
          <Row gutter={[24, 24]}>
            <Col sm={24} xs={24} md={18} lg={18}>
              <Form.Item
                colon={false}
                name="title"
                rules={[
                  {
                    required: true,
                    message: "A title is required",
                  },
                  { max: 65 },
                ]}
                label="Title"
              >
                <Input />
              </Form.Item>
            </Col>
          </Row>
        </Form>
      </div>
    </div>
  ) : (
    <Loader />
  );
};

const mapStateToProps = (state) => ({
  characters: state.characters.userCharacters,
  confirmMessage: state.stories.message,
  storyId: state.stories.storyId,
  loading: state.stories.loadingStory,
  story: state.stories.story,
});

export default connect(mapStateToProps, {
  getUserCharacters,
  editStory,
  getStory,
})(EditStory);
