import { db } from "../fbConfig";

import { GET_PROFILE } from "../../utils/constants";

export const getProfile = () => (dispatch) => {
  db.collection("users")
    .doc("0K9FO3KtCZOm2VCItZrssTJ8xbE3")
    .onSnapshot((doc) => {
      return dispatch({
        user: { id: doc.id, ...doc.data() },
      });
    });
};
