import React from "react";
import {
  Row,
  Col,
  Space,
  Divider,
  Form,
  Spin,
  message,
  Input,
  Select,
} from "antd";
import { connect } from "react-redux";
import { useRouter } from "next/router";

import {
  CATEGORIES,
  COPYRIGHTS,
  LANGUAGES,
  STATUS,
} from "../../utils/constants";
import { useAuth } from "../../hooks/userHooks";
import { getUserCharacters } from "../../redux/actions/charactersActions";
import { addStory } from "../../redux/actions/storiesActions";

import InputGroup from "../../components/common/Input";
import SelectGroup from "../../components/common/Select";
import Tags from "../../components/forms/Tags";
import UploadImage from "../../components/forms/UploadImage";
import Loader from "../../components/common/Loader";
import RadioButon from "../../components/forms/RadioButon";

const NewStory = () => {
  const auth = useAuth();
  const [form] = Form.useForm();
  const router = useRouter();

  const [tags, setTags] = React.useState([]);

  const submit = (values) => {
    console.log(values);
  };

  const onFailed = (error) => {
    const errorName = error.errorFields[0].name.toString();
    form.scrollToField(errorName);
    //message.error("There are errors");
  };

  return (
    <div className="new-story custom-form">
      <div className="inner">
        <h2 className="side-heading">Add a new story</h2>
        <Form
          layout="vertical"
          scrollToFirstError
          onFinishFailed={onFailed}
          onFinish={submit}
          form={form}
        >
          <Row gutter={[24, 24]}>
            <Col sm={24} xs={24} md={18} lg={18}>
              <Form.Item
                colon={false}
                name="title"
                rules={[
                  {
                    required: true,
                    message: "A title is required",
                  },
                  { max: 65 },
                ]}
                label="Title"
              >
                <Input />
              </Form.Item>
            </Col>
            <Col sm={24} xs={24} lg={6} md={6}>
              <Form.Item
                name="categories"
                label="Categories"
                rules={[
                  {
                    required: true,
                    message: "At least 1 category is required",
                  },
                  {
                    max: 2,
                    type: "array",
                    message: "You can only select up to 2 categories",
                  },
                ]}
              >
                <Select mode="multiple" allowClear maxTagCount={2}>
                  {CATEGORIES?.sort((a, b) => {
                    const keyA = a.name,
                      keyB = b.name;
                    // Compare the 2 dates
                    if (keyA < keyB) return -1;
                    if (keyA > keyB) return 1;
                    return 0;
                  }).map((cat) => (
                    <Option value={cat.value} key={cat.value}>
                      {cat.name}
                    </Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
          </Row>
          <Row gutter={[24, 24]}>
            <Col md={8} sm={24} xs={24} lg={8}>
              <Form.Item
                name="language"
                label="Language"
                rules={[
                  {
                    required: true,
                    message: "A language is required",
                  },
                ]}
              >
                <Select>
                  {LANGUAGES?.sort((a, b) => {
                    const keyA = a.name,
                      keyB = b.name;
                    // Compare the 2 dates
                    if (keyA < keyB) return -1;
                    if (keyA > keyB) return 1;
                    return 0;
                  }).map((lang) => (
                    <Option value={lang.value} key={lang.value}>
                      {lang.name}
                    </Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
            <Col md={8} sm={24} xs={24} lg={8}>
              <Form.Item
                name="status"
                label="Status"
                rules={[
                  {
                    required: true,
                    message: "A status is required",
                  },
                ]}
              >
                <Select>
                  {STATUS.map((stat) => (
                    <Option value={stat.value} key={stat.value}>
                      {stat.name}
                    </Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
            <Col md={8} sm={24} xs={24} lg={8}>
              <Form.Item
                name="copyright"
                label="Copyright"
                rules={[
                  {
                    required: true,
                    message: "A copyright is required",
                  },
                ]}
              >
                <Select>
                  {COPYRIGHTS.map((copy) => (
                    <Option value={copy.value} key={copy.value}>
                      {copy.name}
                    </Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
          </Row>
          <Form.Item
            label="Summary"
            name="summary"
            rules={[
              {
                max: 180,
                message: "Your summary cannot exceed 180 characters",
              },
            ]}
          >
            <Input.TextArea
              autoSize={{ minRows: 5 }}
              showCount={true}
              maxLength={180}
            ></Input.TextArea>
          </Form.Item>
          <Row justify="space-between" gutter={[24, 24]}>
            <RadioButon
              lg={4}
              sm={12}
              xs={24}
              md={8}
              name="public"
              label="Visibility"
              num1="3"
              num2="4"
              label1="Public"
              label2="Private"
            />
            <RadioButon
              lg={4}
              sm={12}
              xs={24}
              md={8}
              name="mature"
              label="Mature content ?"
              num1="1"
              num2="2"
              label1="Yes"
              label2="No"
            />
            <Col lg={8} md={12} sm={24} xs={24}>
              <Form.Item label="tags" name="tags">
                <Tags storyInfo={storyInfo} tags={tags} setTags={setTags} />
              </Form.Item>
            </Col>
          </Row>
          <Divider />
          <button type="submit">Create</button>
        </Form>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({
  characters: state.characters.userCharacters,
  confirmMessage: state.stories.message,
  storyId: state.stories.storyId,
  loading: state.stories.loadingStory,
});

export default connect(mapStateToProps, { getUserCharacters, addStory })(
  NewStory
);
