import React from "react";
import { Row, Col, Space, Tag, Input, Tooltip } from "antd";
import { Upload, message } from "antd";
import { LoadingOutlined, PlusOutlined } from "@ant-design/icons";

import {
  CATEGORIES,
  COPYRIGHTS,
  LANGUAGES,
  STATUS,
} from "../../utils/constants";
import InputGroup from "../../components/common/Input";
import SelectGroup from "../../components/common/Select";

function getBase64(img, callback) {
  const reader = new FileReader();
  reader.addEventListener("load", () => callback(reader.result));
  reader.readAsDataURL(img);
}

function beforeUpload(file) {
  const isJpgOrPng = file.type === "image/jpeg" || file.type === "image/png";
  if (!isJpgOrPng) {
    message.error("You can only upload JPG/PNG file!");
  }
  const isLt2M = file.size / 1024 / 1024 < 2;
  if (!isLt2M) {
    message.error("Image must smaller than 2MB!");
  }
  return isJpgOrPng && isLt2M;
}

const newStory = () => {
  const editInput = React.createRef();
  const editedInput = React.createRef();
  const [loading, setLoading] = React.useState(false);
  const [imageUrl, setImageUrl] = React.useState("");
  const [tags, setTags] = React.useState([]);
  const [inputVisible, setInputVisible] = React.useState(false);
  const [inputValue, setInputValue] = React.useState("");
  const [editInputIndex, SetEditInputIndex] = React.useState(-1);
  const [editInputValue, SetEditInputValue] = React.useState("");

  const handleChange = (info) => {
    if (info.file.status === "uploading") {
      setLoading(true);
      return;
    }
    if (info.file.status === "done") {
      // Get this url from response in real world.
      getBase64(info.file.originFileObj, (imageUrl) => {
        setLoading(false);
        setImageUrl(imageUrl);
      });
    }
  };

  const uploadButton = (
    <div>
      {loading ? <LoadingOutlined /> : <PlusOutlined />}
      <div style={{ marginTop: 8 }}>Upload</div>
    </div>
  );

  const handleClose = (removedTag) => {
    const tags = tags.filter((tag) => tag !== removedTag);
    setTags(tags);
  };

  const showInput = () => {
    setInputVisible(true);
    editInput.focus();
  };

  const handleInputChange = (e) => {
    setInputVisible(e.target.value);
  };

  const handleInputConfirm = () => {
    if (inputValue && tags.indexOf(inputValue) === -1) {
      tags = [...tags, inputValue];
    }
    setTags(tags);
    setInputVisible(false);
    setInputVisible("");
  };

  const handleEditInputChange = (e) => {
    SetEditInputValue(e.target.value);
  };

  const handleEditInputConfirm = () => {
    const newTags = [...tags];
    newTags[editInputIndex] = editInputValue;
    setTags(newTags);
    editInputValue("");
    seteditInputIndex(-1);
  };

  const saveInputRef = (input) => {
    editInput = input;
  };

  saveEditInputRef = (input) => {
    editedInput = input;
  };

  console.log(imageUrl);
  return (
    <div className="new-story">
      <h2 className="side-heading">Add a new story</h2>
      <form className="form-group">
        <Row gutter={[24, 24]}>
          <InputGroup
            md={18}
            type="text"
            name="title"
            placeholder="Enter a title"
          />
          <SelectGroup md={6} name="categories" options={CATEGORIES} />
        </Row>
        <Row gutter={[24, 24]}>
          <SelectGroup md={8} name="language" options={LANGUAGES} />
          <SelectGroup md={8} name="status" options={STATUS} />
          <SelectGroup md={8} name="copyright" options={COPYRIGHTS} />
        </Row>
        <textarea
          name="summary"
          id="summary"
          className="form-input"
          placeholder="Summarize your story here"
        ></textarea>
        <Row>
          <Col md={8}>
            <div className="input-group">
              <label htmlFor="public">Mature content</label>

              <div className="radio">
                <Space size={32}>
                  <div className="radio__1">
                    <input
                      id="radio-1"
                      type="radio"
                      name="radio"
                      value={true}
                    />
                    <label htmlFor="radio-1"></label>
                    <p>Yes</p>
                  </div>

                  <div className="radio__2">
                    <input
                      id="radio-2"
                      type="radio"
                      name="radio"
                      value={false}
                      checked
                    />
                    <label htmlFor="radio-2"></label>
                    <p>No</p>
                  </div>
                </Space>
              </div>
            </div>
          </Col>
          <Col md={8}>
            <div className="input-group">
              <label htmlFor="public">Visibilty</label>
              <div className="radio">
                <Space size={32}>
                  <div className="radio__1">
                    <input
                      id="radio-1"
                      type="radio"
                      name="radio"
                      value={true}
                    />
                    <label htmlFor="radio-1"></label>
                    <p>Public</p>
                  </div>

                  <div className="radio__2">
                    <input
                      id="radio-2"
                      type="radio"
                      name="radio"
                      value={false}
                      checked
                    />
                    <label htmlFor="radio-2"></label>
                    <p>Private</p>
                  </div>
                </Space>
              </div>
            </div>
          </Col>
          <Col md={8}>
            <div className="input-group">
              <label htmlFor="tags">Tags</label>
            </div>
          </Col>
          <Col md={8}>
            <div className="input-group">
              <label htmlFor="cover">Upload a cover</label>
              <Upload
                name="cover"
                listType="picture-card"
                className="cover-uploader"
                showUploadList={false}
                action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                beforeUpload={beforeUpload}
                onChange={handleChange}
              >
                {imageUrl ? (
                  <img src={imageUrl} alt="cover" style={{ width: "100%" }} />
                ) : (
                  uploadButton
                )}
              </Upload>
              <input
                type="text"
                className="form-input"
                placeholder="image copyright"
              />
            </div>
          </Col>
        </Row>
      </form>
    </div>
  );
};

export default newStory;
