import React from "react";
import { Pagination as Paginate, Divider, Form } from "antd";

import { StoriesGrid } from "./Grid";
import SortInput from "../profile/SortInput";

const Pagination = ({
  data,
  xs,
  sm,
  lg,
  xl,
  md,
  type,
  gutter,
  columnsCountBreakPoints,
  itemType,
}) => {
  const [pagination, setPagination] = React.useState({
    currentPage: 1,
    datasPerPage: 12,
  });
  const { currentPage, datasPerPage } = pagination;
  const indexOfLastData = currentPage * datasPerPage;
  const indexOfFirstData = indexOfLastData - datasPerPage;
  const [currentDatas, setCurrentDatas] = React.useState(
    data.slice(indexOfFirstData, indexOfLastData)
  );

  console.log(form.getFieldsValue("sort"));

  const [form] = Form.useForm();
  const [datas, setDatas] = React.useState(currentDatas);
  React.useEffect(() => {
    if (form.getFieldsValue("sort") === "date-asc") {
      setCurrentDatas(
        currentDatas.sort((a, b) => {
          return typeof a.createdAt === "object"
            ? new Date(a.createdAt.seconds * 1000) -
                new Date(b.createdAt.seconds * 1000)
            : new Date(a.createdAt) - new Date(b.createdAt);
        })
      );
    } else if (form.getFieldsValue("sort") === "date-desc") {
      setCurrentDatas(
        currentDatas.sort((a, b) => {
          return typeof a.createdAt === "object"
            ? new Date(b.createdAt.seconds * 1000) -
                new Date(a.createdAt.seconds * 1000)
            : new Date(b.createdAt) - new Date(a.createdAt);
        })
      );
    } else if (form.getFieldsValue("sort" === "name-desc")) {
      setCurrentDatas(
        currentDatas.sort((a, b) => {
          const keyA = a.title,
            keyB = b.title;
          if (keyA < keyB) return -1;
          if (keyA > keyB) return 1;
          return 0;
        })
      );
    } else if (form.getFieldsValue("sort" === "name-asc")) {
      setCurrentDatas(
        currentDatas.sort((a, b) => {
          const keyA = a.title,
            keyB = b.title;
          if (keyA > keyB) return -1;
          if (keyA < keyB) return 1;
          return 0;
        })
      );
    }
  }, [form.getFieldsValue("sort")]);

  const handleClick = (e) =>
    setPagination({ ...pagination, currentPage: Number(e) });

  const renderDatas =
    itemType === "stories" ? (
      <StoriesGrid
        gutter={gutter}
        type={type}
        stories={datas}
        columnsCountBreakPoints={columnsCountBreakPoints}
      />
    ) : null;

  let pageNumbers = [];
  for (let i = 1; i <= Math.ceil(data.length / datasPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <React.Fragment>
      <div className="ctrl-section">
        <Paginate
          size="small"
          showQuickJumper
          onChange={handleClick}
          total={data.length}
          pageSize={datasPerPage}
          showTotal={(total, range) =>
            `${range[0]}-${range[1]} of ${total} items`
          }
        />
        <SortInput form={form} />
      </div>
      <div></div>
      <Divider />
      {renderDatas}
      <Divider />
      <Paginate
        size="small"
        showQuickJumper
        onChange={handleClick}
        total={data.length}
        pageSize={datasPerPage}
        showTotal={(total, range) =>
          `${range[0]}-${range[1]} of ${total} items`
        }
      />
    </React.Fragment>
  );
};

export default Pagination;
