import React from "react";
import { connect } from "react-redux";
import Link from "next/link";
import { Row, Col, Divider, Space } from "antd";

import { getHomeStories } from "../redux/actions/storiesActions";
import { getPopularCharacters } from "../redux/actions/charactersActions";
import { getPopularUsers } from "../redux/actions/userActions";

import { CharacterGrid, MiniGrid } from "../components/common/Grid";
import LoadingScreen from "../components/hoc/LoadingScreen";

const Home = (props) => {
  const { loading, stories, characters, users } = props;

  React.useEffect(() => {
    props.getHomeStories();
    props.getPopularCharacters();
    props.getPopularUsers();
  }, []);

  return (
    <LoadingScreen loading={loading}>
      <div className="home-page">
        <section>
          <h3 className="side-heading">Popular Stories</h3>
          <div className="featured-stories">
            <Row>
              {stories.map((story, i) => (
                <Col lg={6} md={12} sm={24} xs={24}>
                  <div className="featured-story" key={story.id}>
                    <Link href={`/story/${story.id}`}>
                      <a>
                        <Row>
                          <Col
                            span={24}
                            lg={{ order: i % 2 === 0 ? 1 : 2 }}
                            md={{ order: i % 2 === 0 ? 1 : 2 }}
                            sm={{ order: 1 }}
                          >
                            <img
                              src={story.banner}
                              alt={story.title}
                              data-aos={i % 2 === 0 ? "fade-up" : "fade-down"}
                            />
                          </Col>
                          <Col
                            span={24}
                            lg={{ order: i % 2 === 0 ? 2 : 1 }}
                            md={{ order: i % 2 === 0 ? 2 : 1 }}
                            sm={{ order: 2 }}
                          >
                            <div
                              className="story-details"
                              data-aos={i % 2 === 0 ? "fade-down" : "fade-up"}
                            >
                              <div className="cat-block">
                                <span className="tag">{story.category}</span>
                              </div>
                              <h2>{story.title}</h2>
                              <p className="summary">
                                {story?.summary?.length > 150
                                  ? `${story.summary.slice(0, 150)}...`
                                  : story.summary}
                                {!story.summary && "No summary yet"}
                              </p>
                              <div className="count">
                                <span>
                                  <p className="number">
                                    {story.chaptersCount}{" "}
                                  </p>{" "}
                                  <ion-icon name="book"></ion-icon>
                                </span>
                                <span>
                                  <p className="number">{story.likesCount} </p>{" "}
                                  <ion-icon name="heart"></ion-icon>
                                </span>
                                <span>
                                  <p className="number">
                                    {story.commentsCount}{" "}
                                  </p>{" "}
                                  <ion-icon name="chatbubble"></ion-icon>
                                </span>
                              </div>
                            </div>
                          </Col>
                        </Row>
                      </a>
                    </Link>
                  </div>
                </Col>
              ))}
            </Row>
          </div>
          <Divider />
        </section>
        <section>
          <h3 className="side-heading">Popular Characters</h3>
          <CharacterGrid
            type="story"
            characters={characters}
            gutter={"16px"}
            columnsCountBreakPoints={{
              1600: 6,
              1200: 4,
              900: 4,
              750: 2,
              600: 2,
              350: 1,
            }}
          />
          <Divider />
        </section>
        <div className="popular-users">
          <div className="popular-users-content">
            <h3 className="side-heading">Popular Users</h3>

            <Row gutter={[16, 32]} justify="space-between">
              {users.map((user) => (
                <Col lg={6} md={12} sm={12} xs={24}>
                  <Link href={`/profile/${user.id}`} key={user.id}>
                    <a>
                      <figure
                        className="user-card home-user-card"
                        data-aos="flip-left"
                      >
                        <Space direction="vertical" size={12} align="center">
                          <div className="img-container">
                            {user.image ? (
                              <img
                                src={user.image}
                                alt={`photo ${user.username}`}
                              />
                            ) : (
                              <ion-icon name="person"></ion-icon>
                            )}
                          </div>
                          <figcaption>
                            <span className="authorName">{user.username}</span>
                            <br />
                            <span>{user.likesCount} followers</span>
                            <br />
                            <div className="social">
                              <Space size={30}>
                                {user.facebook && (
                                  <Link href={user?.facebook}>
                                    <a target="_blank">
                                      <div className="icon custom-icon fb-icon">
                                        <ion-icon name="logo-facebook"></ion-icon>
                                      </div>
                                    </a>
                                  </Link>
                                )}
                                {user.twitter && (
                                  <Link href={user?.twitter}>
                                    <a target="_blank">
                                      <div className="icon custom-icon twitter-icon">
                                        <ion-icon name="logo-twitter"></ion-icon>
                                      </div>
                                    </a>
                                  </Link>
                                )}
                                {user.instagram && (
                                  <Link href={user?.instagram}>
                                    <a target="_blank">
                                      <div className="icon custom-icon instagram-icon">
                                        <ion-icon name="logo-instagram"></ion-icon>
                                      </div>
                                    </a>
                                  </Link>
                                )}
                                {user.deviantart && (
                                  <Link href={user?.deviantart}>
                                    <a target="_blank">
                                      <div className="icon custom-icon deviantart-icon">
                                        <ion-icon name="logo-deviantart"></ion-icon>
                                      </div>
                                    </a>
                                  </Link>
                                )}
                              </Space>
                            </div>
                          </figcaption>
                        </Space>
                      </figure>
                    </a>
                  </Link>
                </Col>
              ))}
            </Row>
          </div>
        </div>
      </div>
    </LoadingScreen>
  );
};

const mapStateToProps = (state) => ({
  stories: state.stories.homeStories,
  characters: state.characters.popularCharacters,
  users: state.auth.popularUsers,
  loading: state.stories.loading,
});

export default connect(mapStateToProps, {
  getHomeStories,
  getPopularCharacters,
  getPopularUsers,
})(Home);
