import React from "react";
import { Row, Col, Space, Divider, Form } from "antd";

import {
  CATEGORIES,
  COPYRIGHTS,
  LANGUAGES,
  STATUS,
} from "../../utils/constants";
import InputGroup from "../../components/common/Input";
import SelectGroup from "../../components/common/Select";
import Tags from "../../components/forms/Tags";
import UploadImage from "../../components/forms/UploadImage";

const newStory = () => {
  const [storyInfo, setStoryInfo] = React.useState({
    title: "",
    categories: [],
    language: "",
    status: "",
    copyright: "",
    summary: "",
    mature: false,
    public: true,
    tags: [],
    mainCharacters: [],
    secondaryCharacters: [],
    image: "",
    imageCopyright: "",
  });
  console.log(storyInfo);
  return (
    <div className="new-story custom-form">
      <div className="inner">
        <h2 className="side-heading">Add a new story</h2>
        <Form layout="vertical" className="form-group">
          <Row gutter={[24, 24]}>
            <InputGroup
              sm={24}
              xs={24}
              md={18}
              lg={18}
              type="text"
              name="title"
              placeholder="Enter a title"
              value={storyInfo.title}
              onChangeFunc={setStoryInfo}
              storyInfo={storyInfo}
            />
            <SelectGroup
              sm={24}
              xs={24}
              lg={6}
              md={6}
              name="categories"
              options={CATEGORIES}
              value={storyInfo.categories}
              onChangeFunc={setStoryInfo}
              storyInfo={storyInfo}
              type="multiple"
            />
          </Row>
          <Row gutter={[24, 24]}>
            <SelectGroup
              md={8}
              sm={24}
              xs={24}
              lg={8}
              name="language"
              options={LANGUAGES}
              value={storyInfo.language}
              onChangeFunc={setStoryInfo}
              storyInfo={storyInfo}
              type="single"
            />
            <SelectGroup
              md={8}
              sm={24}
              xs={24}
              lg={8}
              name="status"
              options={STATUS}
              info={"info about status"}
              value={storyInfo.status}
              onChangeFunc={setStoryInfo}
              storyInfo={storyInfo}
              type="single"
            />
            <SelectGroup
              md={8}
              sm={24}
              xs={24}
              lg={8}
              name="copyright"
              options={COPYRIGHTS}
              info={"info about copyright"}
              value={storyInfo.copyright}
              onChangeFunc={setStoryInfo}
              storyInfo={storyInfo}
              type="single"
            />
          </Row>
          <div className="input-group">
            <label htmlFor="summary">Summary</label>
            <textarea
              rows="5"
              name="summary"
              id="summary"
              className="form-input"
              placeholder="Summarize your story here"
              value={storyInfo.summary}
              onChange={(e) =>
                setStoryInfo({ ...storyInfo, summary: e.target.value })
              }
            ></textarea>
          </div>
          <Row justify="space-between" gutter={[24, 24]}>
            <Col lg={4} md={8} sm={12} xs={24}>
              <div className="input-group">
                <fieldset id="mature">
                  <label className="title-label" htmlFor="public">
                    Mature content
                  </label>

                  <div className="radio">
                    <Space size={32}>
                      <div className="radio__1">
                        <input
                          id="radio-1"
                          type="radio"
                          name="mature"
                          value={true}
                          onChange={() =>
                            setStoryInfo({
                              ...storyInfo,
                              mature: true,
                            })
                          }
                        />
                        <label htmlFor="radio-1"></label>
                        <p>Yes</p>
                      </div>

                      <div className="radio__2">
                        <input
                          id="radio-2"
                          type="radio"
                          name="mature"
                          value={false}
                          onChange={() =>
                            setStoryInfo({
                              ...storyInfo,
                              mature: false,
                            })
                          }
                        />
                        <label htmlFor="radio-2"></label>
                        <p>No</p>
                      </div>
                    </Space>
                  </div>
                </fieldset>
              </div>
            </Col>
            <Col lg={4} md={8} sm={12} xs={24}>
              <div className="input-group">
                <fieldset id="public">
                  <label className="title-label" htmlFor="public">
                    Visibilty
                  </label>
                  <div className="radio">
                    <Space size={32}>
                      <div className="radio__1">
                        <input
                          id="radio-3"
                          type="radio"
                          name="public"
                          value={true}
                          onClick={() =>
                            setStoryInfo({
                              ...storyInfo,
                              mature: true,
                            })
                          }
                        />
                        <label htmlFor="radio-3"></label>
                        <p>Public</p>
                      </div>

                      <div className="radio__2">
                        <input
                          id="radio-4"
                          type="radio"
                          name="public"
                          value={false}
                          onClick={() =>
                            setStoryInfo({
                              ...storyInfo,
                              public: false,
                            })
                          }
                        />
                        <label htmlFor="radio-4"></label>
                        <p>Private</p>
                      </div>
                    </Space>
                  </div>
                </fieldset>
              </div>
            </Col>
            <Col lg={8} md={12} sm={24} xs={24}>
              <div className="input-group">
                <label htmlFor="maincharacters">Main Characters</label>
                <Row>
                  <Col span={22}>
                    <select className="form-input select-add-input">
                      <option value="Caroline">Caroline</option>
                    </select>
                  </Col>
                  <Col span={2}>
                    <div className="form-input select-add-input-iconbox">
                      <span>+</span>
                    </div>
                  </Col>
                  <small>You can add up to 5 main characters</small>
                </Row>
              </div>
            </Col>
            <Col lg={8} md={12} sm={24} xs={24}>
              <label className="title-label" htmlFor="tags">
                Tags
              </label>
              <Tags />
            </Col>
          </Row>
          <Divider />
          <Row gutter={[24, 24]}>
            <Col md={8}>
              <div className="input-group">
                <label htmlFor="image">Upload a cover</label>
                <UploadImage />
              </div>
            </Col>
          </Row>
          <Divider />
          <button type="submit">Create</button>
        </Form>
      </div>
    </div>
  );
};

export default newStory;
