import React from "react";
import { Pagination as Paginate, Divider } from "antd";

import { StoriesGrid } from "./Grid";

const Pagination = ({ data, xs, sm, lg, xl, md, type, gutter, itemType }) => {
  const [pagination, setPagination] = React.useState({
    currentPage: 1,
    datasPerPage: 12,
  });
  const { currentPage, datasPerPage } = pagination;
  const indexOfLastData = currentPage * datasPerPage;
  const indexOfFirstData = indexOfLastData - datasPerPage;
  const currentDatas = data.slice(indexOfFirstData, indexOfLastData);

  const handleClick = (e) =>
    setPagination({ ...pagination, currentPage: Number(e) });

  const renderDatas =
    itemType === "stories" ? (
      <StoriesGrid
        xs={xs}
        lg={lg}
        sm={sm}
        xl={xl}
        md={md}
        gutter={gutter}
        type={type}
        stories={currentDatas}
      />
    ) : null;

  let pageNumbers = [];
  for (let i = 1; i <= Math.ceil(data.length / datasPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <React.Fragment>
      <Paginate
        size="small"
        showQuickJumper
        showSizeChanger
        onChange={handleClick}
        total={data.length}
        pageSize={datasPerPage}
        showTotal={(total, range) =>
          `${range[0]}-${range[1]} of ${total} items`
        }
      />
      <Divider />
      {renderDatas}
      <Divider />
      <Paginate
        size="small"
        showSizeChanger
        showQuickJumper
        onChange={handleClick}
        total={data.length}
        pageSize={datasPerPage}
        showTotal={(total, range) =>
          `${range[0]}-${range[1]} of ${total} items`
        }
      />
    </React.Fragment>
  );
};

export default Pagination;
