import React from "react";

import { Row, Col, Divider } from "antd";

import CharacterCard from "../common/CharacterCard";
import Pagination from "../common/Pagination";
import SortInput from "./SortInput";

const Characters = ({ characters, type }) => {
  const [data, setData] = React.useState([]);

  React.useEffect(() => {
    setData(characters);
  }, [characters]);

  const options = [
    { name: "Date (ascending)", value: "date-asc" },
    { name: "Date (descending)", value: "date-desc" },
    { name: "Firstname (ascending)", value: "firstname-asc" },
    { name: "Firstname (descending)", value: "firstname-desc" },
    { name: "Lastname (ascending)", value: "lastname-asc" },
    { name: "Lastname (descending)", value: "lastname-desc" },
  ];

  return (
    <div className="profile-characters">
      {/* <Row wrap={true} gutter={[16, 16]}>
        {characters?.map((chara) => (
          <Col key={chara.id} xxl={4} xl={6} lg={8} sm={12} md={8} xs={24}>
            <CharacterCard type={type} character={chara} />
          </Col>
        ))}
      </Row> */}
      <SortInput
        type="characters"
        itemData={characters}
        title="Characters"
        setData={setData}
        options={options}
      />
      <Divider />
      <Pagination
        data={data}
        type={type}
        gutter={"16px"}
        itemType="characters"
        columnsCountBreakPoints={{ 350: 1, 750: 2, 900: 3, 1200: 4, 1600: 6 }}
      />
    </div>
  );
};

export default Characters;
