import { Row, Col, Form, Select, Input, Divider } from "antd";

import CharacterCard, { CharacterGrid } from "../common/CharacterCard";

const Relatives = ({ characters }) => {
  const [relations, setRelations] = React.useState([]);
  const [currentChar, setCurrentChar] = React.useState("");
  const [currentRel, setCurrentRel] = React.useState("");
  const [selectedCharacters, setSelectedCharacters] = React.useState([]);
  const [selectedArr, setSelectedArr] = React.useState([]);

  const addRelation = () => {
    let newRelations = [...relations];
    if (currentChar && currentRel) {
      newRelations = [
        ...newRelations,
        { character: currentChar, relation: currentRel },
      ];
      setSelectedCharacters([
        ...selectedCharacters,
        {
          ...characters.find((c) => c.id === currentChar),
          relation: currentRel,
        },
      ]);
      setSelectedArr([...selectedArr, currentChar]);
    }

    setRelations(newRelations);
    setCurrentChar("");
    setCurrentRel("");
  };

  console.log(relations);

  return (
    <Form.Item label="Relationships">
      <Row align="bottom" gutter={[24, 24]}>
        <Col md={11} sm={24}>
          <Select
            placeholder="Select a character"
            value={currentChar}
            onChange={(val) => setCurrentChar(val)}
          >
            <Option value="none" disabled>
              Select a character
            </Option>
            {characters
              ?.filter((c) => !relations.includes(c.id))
              .map((c) => (
                <Option
                  key={c.id}
                  value={c.id}
                >{`${c.firstname} ${c.lastname}`}</Option>
              ))}
          </Select>
        </Col>
        <Col md={11} sm={24}>
          <Input
            value={currentRel}
            placeholder="Type of relationship"
            onChange={(e) => setCurrentRel(e.target.value)}
          />
        </Col>
        <Col md={2} sm={24}>
          <div className="add-btn" onClick={addRelation}>
            Add
          </div>
        </Col>
      </Row>
      <Divider />
      <Row gutter={[16, 16]}>
        {selectedCharacters?.map((rel) => (
          <Col lg={4} md={8} sm={12} xs={24} key={rel.id}>
            <CharacterCard type="add" character={rel} />
          </Col>
        ))}
      </Row>
    </Form.Item>
  );
};

export default Relatives;
