import React from "react";
import { connect } from "react-redux";
import { Form, message } from "antd";
import { useRouter } from "next/router";

import { useAuth } from "../../../../../hooks/userHooks";
import {
  getStory,
  getChapter,
  editChapter,
  getUserLocations,
} from "../../../../../redux/actions/storiesActions";
import { getUserCharacters } from "../../../../../redux/actions/charactersActions";

import ChapterForm from "../../../../../components/forms/ChapterForm";
import LoadingScreen from "../../../../../components/hoc/LoadingScreen";
import RedirectComp from "../../../../../components/hoc/RedirectComp";
import { arr_diff } from "../../../../../utils/helpers";

const EditChapter = (props) => {
  const [form] = Form.useForm();
  const {
    characters,
    loading,
    locations,
    loadingChapter,
    chapter,
    chapterExists,
    editMessage,
    story,
  } = props;
  const auth = useAuth();
  const router = useRouter();
  const [body, setBody] = React.useState("");

  React.useEffect(() => {
    props.getStory(router.query.id);
    props.getChapter(router.query.id, router.query.chapid);
  }, [router.query.id, router.query.chapid]);

  React.useEffect(() => {
    if (auth.user) {
      props.getUserCharacters(auth.user.uid);
      props.getUserLocations(auth.user.uid);
    }
  }, [auth]);

  React.useEffect(() => {
    if (editMessage) {
      message.success(editMessage);
    }
  }, [editMessage]);

  const submit = (values) => {
    delete values.currentChar;
    delete values.currentLoc;

    const originArr = chapter.characters; // Array d'ids
    let secondaryCharacters = [...story.secondaryCharacters]; // Array d'objets contenant les perso et le nombre de fois
    let charInChapter = values.characters.filter(
      (c) => !story.mainArr.includes(c)
    ); // Array contenant les nouveaux perso moins les mains
    let newArr = arr_diff(originArr, charInChapter); // Array contenant tous les changements à traiter

    // Vérifie si le perso est nouvellement ajouté ou retiré
    originArr.forEach((char) => {
      const index = secondaryCharacters.findIndex((c) => c.id === char);
      if (!newArr.includes(char)) {
        secondaryCharacters[index].times = secondaryCharacters[index].times - 1;
      }
    });

    newArr.forEach((char) => {
      const index = secondaryCharacters.findIndex((c) => c.id === char);
      if (index === -1) {
        secondaryCharacters.push({ id: char, times: 1 });
      }
    });
    secondaryCharacters = secondaryCharacters.filter((c) => c.times > 0);
    // charInChapter.forEach((char) => {
    //   const index = secondaryCharacters.findIndex((c) => c.id === char);
    //
    // });

    //

    // if (index === -1) {
    //   secondaryCharacters.push({ id: char, times: 1 });
    // }

    console.log(secondaryCharacters);

    // props.editChapter(
    //   {
    //     ...values,
    //     body,
    //     status: values.status === "true",
    //   },
    //   router.query.id,
    //   router.query.chapid,
    //   secondaryCharacters
    // );
  };

  return (
    <LoadingScreen loading={loading}>
      <RedirectComp type="404" condition={chapterExists}>
        <RedirectComp
          type="403"
          condition={auth.user && auth.user.uid === chapter.authorId}
        >
          <div className="custom-form">
            <div className="inner">
              <h2 className="side-heading">Add a new chapter</h2>
              <ChapterForm
                submit={submit}
                form={form}
                characters={characters}
                locations={locations}
                body={body}
                setBody={setBody}
                type="edit"
                loadingChapter={loadingChapter}
                loading={loading}
                chapter={chapter}
              />
            </div>
          </div>
        </RedirectComp>
      </RedirectComp>
    </LoadingScreen>
  );
};

const mapStateToProps = (state) => ({
  chapter: state.stories.chapter,
  story: state.stories.story,
  characters: state.characters.userCharacters,
  loading: state.stories.loading,
  locations: state.stories.userLocations,
  loadingChapter: state.stories.loadingChapter,
  chapterExists: state.stories.chapterExists,
  editMessage: state.stories.message,
});

export default connect(mapStateToProps, {
  getChapter,
  getUserCharacters,
  getUserLocations,
  editChapter,
  getStory,
})(EditChapter);
