import { Form, Select } from "antd";
import React from "react";

import Pagination from "../common/Pagination";
import LoadingScreen from "../hoc/LoadingScreen";

const Stories = ({ stories, loading, type }) => {
  const [value, setValue] = React.useState("date-desc");
  const [data, setData] = React.useState([]);

  React.useEffect(() => {
    console.log("hello");
  }, [stories]);

  React.useEffect(() => {
    let newArr = [];
    if (value === "date-asc") {
      newArr = stories.sort((a, b) => {
        return typeof a.createdAt === "object"
          ? new Date(a.createdAt.seconds * 1000) -
              new Date(b.createdAt.seconds * 1000)
          : new Date(a.createdAt) - new Date(b.createdAt);
      });
      setData(newArr);
    } else if (value === "date-desc") {
      newArr = stories.sort((a, b) => {
        return typeof a.createdAt === "object"
          ? new Date(b.createdAt.seconds * 1000) -
              new Date(a.createdAt.seconds * 1000)
          : new Date(b.createdAt) - new Date(a.createdAt);
      });
      setData(newArr);
    } else if (value === "name-desc") {
      newArr = stories.sort((a, b) => {
        const keyA = a.title.toLowerCase(),
          keyB = b.title.toLowerCase();

        if (keyA > keyB) return -1;
        if (keyA < keyB) return 1;
        return 0;
      });
      setData(newArr);
    } else if (value === "name-asc") {
      newArr = stories.sort((a, b) => {
        const keyA = a.title.toLowerCase(),
          keyB = b.title.toLowerCase();
        if (keyA < keyB) return -1;
        if (keyA > keyB) return 1;
        return 0;
      });
      setData(newArr);
    } else {
      setData(stories);
    }
  }, [value]);

  return (
    <div className="profile-stories">
      <LoadingScreen loading={loading}>
        <Form.Item label="Sort by" name="sort">
          <Select
            value={value}
            onChange={(val) => setValue(val)}
            defaultValue={"date-desc"}
          >
            <Select.Option value="date-asc">Date (ascending)</Select.Option>
            <Select.Option value="date-desc">Date (descending)</Select.Option>
            <Select.Option value="name-asc">Name (ascending)</Select.Option>
            <Select.Option value="name-desc">Name (descending)</Select.Option>
          </Select>
        </Form.Item>

        <Pagination
          data={data}
          type={type}
          gutter={"16px"}
          type={type}
          itemType="stories"
          columnsCountBreakPoints={{ 350: 1, 750: 2, 900: 3, 1200: 4 }}
        />
      </LoadingScreen>
    </div>
  );
};

export default Stories;
