import { Form, Input, Tooltip } from "antd";

const LinkComp = ({ name, label, info, setInfo }) => {
  const validate_url = (url, name) => {
    if (
      /^(https?:\/\/)?((w{3}\.)?)${name}\.com\/(#!\/)?[a-z0-9_]+$/i.test(url)
    ) {
      return true;
    } else if (!url) {
      setInfo({
        ...info,
        [name]: {
          ...info[name],
          error: ``,
        },
      });
    } else
      setInfo({
        ...info,
        [name]: {
          ...info[name],
          error: `The link is not a valid ${name} url`,
        },
      });
  };
  return (
    <Form.Item
      name={name}
      rules={[
        {
          type: "url",
          message: "This field must be a valid url.",
        },
        () => ({
          validator(_, value) {
            if (value.includes(name)) {
              setInfo({ ...info, [name]: { ...info[name], valid: true } });
              return Promise.resolve();
            } else if (value.length === 0) {
              setInfo({ ...info, [name]: { ...info[name], valid: true } });
              return Promise.resolve();
            } else {
              setInfo({ ...info, [name]: { ...info[name], valid: false } });
              return Promise.reject(
                new Error(`The link is not a valid ${label} url`)
              );
            }
          },
        }),
      ]}
    >
      <Input
        addonBefore={
          <Tooltip title={label}>
            <ion-icon name={`logo-${name}`}></ion-icon>
          </Tooltip>
        }
        addonAfter={info[name].valid && <ion-icon name="checkmark"></ion-icon>}
        onChange={(e) => {
          setInfo({
            ...info,
            [name]: { ...info[name], value: e.target.value },
          });
          validate_url(info[name], "name");
        }}
        value={info[name].value}
      />
    </Form.Item>
  );
};

export default LinkComp;
