import { Col, Popover, Select } from "antd";

const { Option } = Select;

const SelectGroup = ({
  xs,
  lg,
  sm,
  md,
  name,
  options,
  info,
  value,
  onChangeFunc,
  storyInfo,
}) => {
  return (
    <Col xs={xs} sm={sm} md={md} lg={lg}>
      <div className="input-group">
        <label htmlFor="genre">
          {name}{" "}
          {info && (
            <Popover title={info} trigger="click">
              <ion-icon name="information-circle"></ion-icon>
            </Popover>
          )}
        </label>
        <Select
          dropdownClassName="form-input"
          mode="multiple"
          allowClear
          style={{ width: "100%" }}
          placeholder="Please select"
          defaultValue={value}
          onChange={(e) =>
            onChangeFunc({ ...storyInfo, [name]: e.target.value })
          }
        >
          {options
            ?.sort((a, b) => {
              const keyA = a.name,
                keyB = b.name;
              // Compare the 2 dates
              if (keyA < keyB) return -1;
              if (keyA > keyB) return 1;
              return 0;
            })
            .map((option) => (
              <Option
                style={{ textTransform: "capitalize" }}
                key={option.value}
                value={option.value}
              >
                {option.name}
              </Option>
            ))}
        </Select>
      </div>
    </Col>
  );
};

export default SelectGroup;
