import React, { Suspense } from "react";
import { connect } from "react-redux";
import { Result, Button, Form, Divider, Row, Col } from "antd";
import { createEditor } from "slate";
import { Slate, Editable, withReact } from "slate-react";

import { useRouter } from "next/router";
import { useAuth } from "../../../../hooks/userHooks";
import { getStory } from "../../../../redux/actions/storiesActions";
import { getUserCharacters } from "../../../../redux/actions/charactersActions";
import withLoading from "../../../../components/hoc/withLoading";
import ChapterForm from "../../../../components/forms/ChapterForm";
import CharacterSelector from "../../../../components/forms/CharacterSelector";
import withAuth from "../../../../components/hoc/withAuth";

const NewChapter = (props) => {
  const router = useRouter();
  const auth = useAuth();
  const [form] = Form.useForm();
  const { characters, storyExists, loading, story, setLoading } = props;
  const editor = React.useMemo(() => withReact(createEditor()), []);

  const [value, setValue] = React.useState([
    {
      type: "paragraph",
      children: [{ text: "A line of text in a paragraph." }],
    },
  ]);

  React.useEffect(() => {
    if (auth.user) {
      props.getUserCharacters();
    }
  }, [auth]);

  React.useEffect(() => {
    setLoading(loading);
  }, [loading]);

  React.useEffect(() => {
    props.getStory(router.query.id);
  }, []);

  const submit = (values) => {
    console.log(values);
  };

  return (
    <withAuth>
      {storyExists ? (
        story.authorId === auth.user && auth.user.uid ? (
          <div className="custom-form">
            <div className="inner">
              <h2 className="side-heading">Add a new chapter</h2>
              <ChapterForm submit={submit} form={form} />
              {/* <Slate
              editor={editor}
              value={value}
              onChange={(newValue) => setValue(newValue)}
            >
              <Editable
                onKeyDown={(event) => {
                  if (event.key === "&") {
                    event.preventDefault();
                    // editor.insertText("and");
                  }
                }}
              />
            </Slate> */}
              <Divider />
              <Row gutter={24}>
                <Col md={12} xs={24} sm={24}>
                  <CharacterSelector
                    label={"Characters in this chapter"}
                    arrInForm={"characters"}
                    form={form}
                    type="chapter"
                    characters={characters}
                    lg1={24}
                  />
                </Col>
              </Row>
            </div>
          </div>
        ) : (
          <Result
            status="403"
            title="403"
            subTitle="Sorry, you are not authorized to access this page."
            extra={<Button type="primary">Back Home</Button>}
          />
        )
      ) : (
        <Result
          status="404"
          title="404"
          subTitle="Sorry, the page you visited does not exist."
          extra={<Button type="primary">Back Home</Button>}
        />
      )}
    </withAuth>
  );
};

const mapStateToProps = (state) => ({
  characters: state.characters.userCharacters,
  storyExists: state.stories.storyExists,
  loading: state.stories.loading,
  story: state.stories.story,
});

export default connect(mapStateToProps, {
  getUserCharacters,
  getStory,
})(withLoading(NewChapter));
