import { Form, Tabs, Input, Row, Col, Modal, Button } from "antd";
import { dummy } from "../../utils/dummy";
import {
  LockOutlined,
  UserOutlined,
  LinkOutlined,
  FileImageOutlined,
} from "@ant-design/icons";
import LinkComp from "./LinkComp";

const { TabPane } = Tabs;

const Settings = ({ profile }) => {
  const [form] = Form.useForm();

  const submit = (values) => console.log(values);
  return (
    <>
      <Modal
        title="Settings"
        visible={settings}
        onCancel={() => openSettings(false)}
        footer={[
          <Button key="submit" type="primary" htmlType="submit">
            Submit
          </Button>,
        ]}
      >
        <Settings profile={profile} openSettings={openSettings} />
      </Modal>
      <div className="settings">
        <Form
          form={form}
          onFinish={submit}
          layout="vertical"
          initialValues={{
            username: profile.username,
            instagram: profile.instagram,
            facebook: profile.facebook,
            twitter: profile.twitter,
            deviantart: profile.deviantart,
            image: profile.image,
          }}
        >
          <Tabs defaultActiveKey="1">
            <TabPane
              tab={
                <span>
                  <UserOutlined />
                  Username
                </span>
              }
              key="1"
            >
              <Form.Item name="username" label="Change your username">
                <Input />
              </Form.Item>
            </TabPane>
            <TabPane
              tab={
                <span>
                  <LockOutlined />
                  Password
                </span>
              }
              key="2"
            >
              <Form.Item label="Actual password" name="password">
                <Input.Password />
              </Form.Item>
              <Form.Item label="New password" name="confirm-password">
                <Input.Password />
              </Form.Item>
            </TabPane>
            <TabPane
              tab={
                <span>
                  <LinkOutlined />
                  Links
                </span>
              }
              key="3"
            >
              <LinkComp name={"facebook"} label={"Facebook"} />
              <LinkComp name={"twitter"} label={"Twitter"} />
              <LinkComp name={"instagram"} label={"Instagram"} />
              <LinkComp name={"deviantart"} label={"Deviantart"} />
            </TabPane>
            <TabPane
              tab={
                <span>
                  <FileImageOutlined />
                  Avatar
                </span>
              }
              key="4"
            ></TabPane>
          </Tabs>
        </Form>
      </div>
    </>
  );
};

export default Settings;
