import React from "react";
import { connect } from "react-redux";
import { Form } from "antd";

import { getUserStories } from "../../redux/actions/storiesActions";
import { useAuth } from "../../hooks/userHooks";

import LocationForm from "../../components/forms/LocationForm";

const newLocation = (props) => {
  const { stories } = props;
  const [form] = Form.useForm();
  const auth = useAuth();

  React.useEffect(() => {
    if (auth.user) {
      props.getUserStories();
    }
  }, [auth]);

  return (
    <div className="new-location custom-form">
      <div className="inner">
        <h2 className="side-heading">Add a new location</h2>
        <LocationForm form={form} stories={stories} type="add" />
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({
  stories: state.stories.userStories,
});

export default connect(mapStateToProps, { getUserStories })(newLocation);
