import { Col, Space, Form } from "antd";

const RadioButon = ({
  lg,
  sm,
  xs,
  md,
  name,
  label,
  num1,
  num2,
  label1,
  label2,
  defaultValue,
}) => {
  return (
    <Col lg={lg} md={md} sm={sm} xs={xs}>
      <Form.Item name={name} label={label}>
        <fieldset id={name}>
          <div className="radio">
            <Space size={32}>
              <div className="radio__1">
                <input
                  id={`radio-${num1}`}
                  type="radio"
                  value={true}
                  checked={defaultValue ? false : true}
                />
                <label htmlFor={`radio-${num1}`}></label>
                <p>{label1}</p>
              </div>
              <div className="radio__2">
                <input
                  id={`radio-${num2}`}
                  type="radio"
                  value={false}
                  checked={defaultValue ? false : true}
                />
                <label htmlFor={`radio-${num2}`}></label>
                <p>{label2}</p>
              </div>
            </Space>
          </div>
        </fieldset>
      </Form.Item>
    </Col>
  );
};

export default RadioButon;
