import React from "react";
import { Row, Col, Space, Divider, Form, Spin, message } from "antd";
import { connect } from "react-redux";
import { useRouter } from "next/router";

import {
  CATEGORIES,
  COPYRIGHTS,
  LANGUAGES,
  STATUS,
} from "../../utils/constants";
import { useAuth } from "../../hooks/userHooks";
import { getUserCharacters } from "../../redux/actions/charactersActions";
import { addStory } from "../../redux/actions/storiesActions";

import InputGroup from "../../components/common/Input";
import SelectGroup from "../../components/common/Select";
import Tags from "../../components/forms/Tags";
import UploadImage from "../../components/forms/UploadImage";
import Loader from "../../components/common/Loader";

const NewStory = () => {
  const auth = useAuth();
  const [form] = Form.useForm();
  const router = useRouter();

  const submit = (values) => {
    console.log(values);
  };

  const onFailed = (error) => {
    const errorName = error.errorFields[0].name.toString();
    window.scroll({ top: 0, left: 0 });
    message.error("There are errors");
  };

  return (
    <div className="new-story custom-form">
      <div className="inner">
        <h2 className="side-heading">Add a new story</h2>
        <Form onFinishFailed={onFailed} onFinish={submit} form={form}>
          <Row>
            <Form.Item
              name="title"
              rules={[{ required: true, message: "A title is required" }]}
              label="Title"
            >
              <div className="input-group">
                <input
                  className="form-input"
                  type="text"
                  id="title"
                  value={form.getFieldValue("title")}
                  onChange={(val) => form.setFieldsValue({ title: val })}
                />
              </div>
            </Form.Item>
            <Form.Item
              name="categories"
              rules={[{ required: true, message: "A category is required" }]}
              label="Categories"
            >
              <div className="input-group">
                <input
                  className="form-input"
                  type="text"
                  id="categories"
                  value={form.getFieldValue("categories")}
                  onChange={(val) => form.setFieldsValue({ categories: val })}
                />
              </div>
            </Form.Item>
          </Row>
          <Divider />
          <button htmlType="submit">Create</button>
        </Form>
      </div>
    </div>
  );
};

export default NewStory;
