import React from "react";
import { Col, Select, Radio, Input, Divider } from "antd";

import { CATEGORIES, LANGUAGES, STATUS } from "../../utils/constants";

const Filter = ({
  setCurrentCategory,
  currentCategory,
  setCurrentStatus,
  currentStatus,
  setCurrentLanguage,
  currentLanguage,
}) => {
  const radioStyle = {
    display: "block",
    height: "30px",
    lineHeight: "30px",
  };

  return (
    <Col span={6}>
      <aside className="filter">
        <h4>Filter</h4>
        <div>
          <h5>by categories</h5>
          <Select
            value={currentCategory}
            onChange={(e) => setCurrentCategory(e.target.value)}
          >
            <Select.Option value="">Choose a category</Select.Option>
            {CATEGORIES.map((c) => (
              <Select.Option key={c.value} value={c.value}>
                {c.name}
              </Select.Option>
            ))}
          </Select>
        </div>
        <Divider />
        <div>
          <h5>by language</h5>
          <Select
            value={currentLanguage}
            onChange={(e) => setCurrentLanguage(e.target.value)}
          >
            <Select.Option value="">Choose a language</Select.Option>
            {LANGUAGES.map((l) => (
              <Select.Option key={l.value} value={l.value}>
                {l.name}
              </Select.Option>
            ))}
          </Select>
        </div>
        <Divider />
        <div>
          <h5>Status: </h5>
          <Radio.Group
            onChange={(e) => setCurrentStatus(e.target.value)}
            value={currentStatus}
          >
            {STATUS.map((s) => (
              <Radio style={radioStyle} value={s.value}>
                {s.name}
              </Radio>
            ))}
          </Radio.Group>
        </div>
      </aside>
    </Col>
  );
};

export default Filter;
