import React from "react";
import { Form, Result, Button } from "antd";
import { connect } from "react-redux";
import { useRouter } from "next/router";

import { useAuth } from "../../../hooks/userHooks";
import { getUserCharacters } from "../../../redux/actions/charactersActions";
import { getStory, editStory } from "../../../redux/actions/storiesActions";

//import Loader from "../../../components/hoc/withLoading";
import StoryForm from "../../../components/forms/StoryForm";
import LoadingScreen from "../../../components/hoc/LoadingScreen";
import RedirectComp from "../../../components/hoc/RedirectComp";

const EditStory = (props) => {
  const auth = useAuth();
  const router = useRouter();
  const { story, loading, characters, loadingStory, storyExists } = props;
  const [form] = Form.useForm();

  const [mature, setMature] = React.useState(false);
  const [visibility, setVisibility] = React.useState(true);

  React.useEffect(() => {
    if (auth.user) {
      props.getUserCharacters(auth.user.uid);
    }
  }, [auth]);

  React.useEffect(() => {
    if (router.query.id) {
      props.getStory(router.query.id);
    }
  }, [router.query.id]);

  const submit = (values) => {
    delete values.currentChar;
    props.editStory(values, router.query.id);
  };

  return (
    <LoadingScreen loading={loading}>
      <RedirectComp type={"404"} condition={storyExists && story.title}>
        <RedirectComp type="403" condition={auth.user.uid === story.authorId}>
          <div className="new-story custom-form">
            <div className="inner">
              <h2 className="side-heading">Edit story: {story.title}</h2>
              <StoryForm
                type="edit"
                submit={submit}
                characters={characters}
                loading={loading}
                form={form}
                mature={mature}
                setMature={setMature}
                visibility={visibility}
                setVisibility={setVisibility}
                story={story}
                loadingStory={loadingStory}
              />
            </div>
          </div>
        </RedirectComp>
      </RedirectComp>
    </LoadingScreen>
  );
};

const mapStateToProps = (state) => ({
  characters: state.characters.userCharacters,
  confirmMessage: state.stories.message,
  storyId: state.stories.storyId,
  loadingStory: state.stories.loadingStory,
  story: state.stories.story,
  loading: state.stories.loading,
  storyExists: state.stories.storyExists,
});

export default connect(mapStateToProps, {
  getUserCharacters,
  editStory,
  getStory,
})(EditStory);
